import React, { useState } from "react";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import Menu from "./Menu";
import {Route, Switch} from "react-router-dom";

const Header = () =>{
    const [time, setTime] = useState();
    const curtime = new Date().toLocaleDateString();
    const getHours = () =>{
        let curtime = new Date().toLocaleTimeString();
        setTime(curtime);
    };  setInterval(getHours);        
    return(
        <>
            <div className="header">
            <div className="container"> 
            <div className="row">                
                <div className="col-lg-4 align-items-center my-3"> 
                    <h1>Google Keep <span className="cuttime"> {curtime} - {time} </span></h1>
                </div>
                <div className="col-lg-8 my-3"> <Menu /> </div>   
            </div>  
            </div>
            </div>
            
            <Switch>
                <Route exact path="/" component={Main} />
                <Route exact path="/App" component={App} />
            </Switch>


        </>
    );
}
export default Header;
