import React, { useState } from "react";
import Header from "./Header";
import CreateNote from "./CreateNote";
import Note from "./Note";
import Footer from "./Footer";

const App = () =>{

    const [addItems, setAddItems] = useState([]);

    const addNote = ((note) =>{
        // alert("I am Clicked");     
        setAddItems((prevData) => {
            return [...prevData, note];            
        })
        .catch((err) => {
            console.log(err);
          });
       
    },[]);

    const onDelete = (id) => {
        console.log("Deleted");
       setAddItems((olddata) => {
        olddata.filter((arraE, indx) => {
            return indx !== id;
            });
        });    
    };
    return(
        <>
        <Header/>
        <CreateNote passNote={addNote}  />  
        {addItems.map((val, index) => {
            return (
                <Note 
                    key={val.id}
                    id={index}
                    title={val.title}
                    content={val.content}
                    deleteItem={onDelete}
            />
            );
        })}
        
        <Footer/>
        </>
    );
}
export default App;
