import React, { useState } from 'react';

const App = () =>{

    // const bg = '#FFE4E1';

    const [bg, setBg] = useState('#FFE4E1');
    const [name, setName] = useState('Click Me');

    const changeBg = () =>{

        setBg('#B0C4DE');
        setName ('Clicked ðŸ˜„');

    };

   

    return(
    
        <>
        <div className = "main_box">

            <div style = { { backgroundColor : bg } }>

            <button onClick = {changeBg}> {name}} </button>
            
            </div>

        </div>
      
        </>
        );
}

export default App;
