import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';


const CreateNote = (props) => {

    const [note, setNote] = useState({
        title: "",
        content: "",
    }); 

    const [expend, setExpend] = useState(false);

    const storeData = (event) =>{
        
        const {name,value} = event.target;

        setNote((preVal) =>{
           return {
                ...preVal,
                [name]: value,
                };
        });
    };

    const getData = () =>{
        props.passNote(note);
        setNote({
            title: "",         
            content: "",
            
        });
    };

    const opentxt = () => {

        expend(true);
    };

    return(
        <>      
        <div className="box_center">
            <div className="content">
            {expend?
                <input 
                type="text" 
                name="title"
                value={note.title} 
                onChange={storeData} 
                placeholder="Title" 
                className="title"
                />  
                : null  }        
            <textarea 
            rows=""
            column=""
            name="content"
            value={note.content} 
            onChange={storeData}
            placeholder="Write a note..."
            onClick={opentxt}
            ></textarea>
        
            <button onClick={getData} className="button"><AddIcon/></button>
        </div>            
    </div>
    </>
    );
}
export default CreateNote;
