import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';
import RemoveIcon from '@material-ui/icons/Remove';
import Button from '@material-ui/core/Button';
import AddCircleOutlineIcon from '@material-ui/icons/AddCircleOutline';


const App = () =>{

    const [num, setNum] = useState(0);

    const incNum = () =>{

        return setNum(num + 1);

    };

    const decNum = () =>{

        if(num <= 0){
            alert("sorry this is minimum amount ðŸ˜²");
            return setNum(0);
        } else{
        return setNum(num - 1);
        }
    };


    return(
        <>

        <div className="main_box">
            <div className="card_box">

                <h1>{num}</h1>
                <div style={{ float : "left"}}><Button onClick={incNum} className="butGreen">  <AddCircleOutlineIcon/>  </Button></div>
                <div style={{ float : "right"}}><Button onClick={decNum} className="butRed">  <RemoveIcon/>  </Button></div>

            </div>
        </div>

        </>
    );
};
export default App;
