import React, {useState} from 'react';

const App = () =>{

    const [letName, setLetName] = useState({

        name1: " ",
        name2: " ",
        field3: " ",
        field4: " ",
    });
 
    
    const fieldvalue = (event) =>{

        const name = event.target.name;
        const value = event.target.value;

        setLetName((preValue) =>{

            if(name === "fname"){
             
                return{
                name1: value,
                name2: preValue.name2,
                field3: preValue.field3,
                field4: preValue.field4,
                
            };
            }else if(name === "lname"){
             
                return{
                name1: preValue.name1,
                name2: value,
                field3: preValue.field3,
                field4: preValue.field4,
            };
        }

    });

};


    const getName = (event) =>{        
        event.preventDefault();
        
    };

    return (

        <div className="main_box">
        <form onSubmit={getName} >
        <div>

        <h2>Hello  {letName.name1} {letName.name2} </h2>
        <input type="text" name="fname" placeholder="Enter your Name" onChange={fieldvalue} value={letName.name1}/>

        <input type="text" name="lname" placeholder="Enter your Lastname" onChange={fieldvalue} value={letName.name2}/>

        <input type="text" name="email" placeholder="Enter your Lastname" onChange={fieldvalue} value={letName.field3}/>


        <input type="text" name="phone" placeholder="Enter your Lastname" onChange={fieldvalue} value={letName.field4}/>

        <button type="submit">Click Me</button>
        </div>

        </form>
        </div>



    );


}
export default App;
