import React, { useState } from "react";
import Header from "./Header";
import CreateNote from "./CreateNote";
import Note from "./Note";
import Footer from "./Footer";

const App = () =>{

    const [addItems, setAddItems] = useState([]);

    const [loading, setLoading] = useState(false);
  const [error, setError] = useState();


    const addNote = (note) =>{
        // alert("I am Clicked");     
        setAddItems((prevData) => {
            return [...prevData, note];            
        });
             
    };

    const onDelete = (id) => {
        alert(id);
        
       setAddItems((olddata) => {
        olddata.filter((arraE, indx) => {
            return indx !== id;
            });
        });

        // .catch((err) => {
        //     setError(err);
        //   })
        //   .finally(() => {
        //     setLoading(false);
        //   });
           
    };
    // if (loading) {
    //     return <p>addItems is loading...</p>;
    //   }
    
    //   if (error || !Array.isArray(addItems)) {
    //     return <p>There was an error loading your addItems!</p>;
    //   }

    return(
        <>
        <Header/>
        <CreateNote passNote={addNote}  />  
        {addItems.map((val, index) => {
            return (
                <Note 
                    key={index}
                    id={index}
                    title={val.title}
                    content={val.content}
                    deleteItem={onDelete}
            />
            );
        })}
        
        <Footer/>
        </>
    );
}
export default App;
