import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';


const CreateNote = () => {

    const [curdata, SetCurdata] = useState({
        title: " ",
        comment: " ",
    });



    const getData = (event) =>{
        
        const [name,value] = event.target;

        SetCurdata ((preVal)=>{
           return {
                ...preVal, curdata,
                [name] : value,
                }
        });
    };



    return(

        <>

                <div className="box_center">
                     
                  <div className="content">
                    <div>
                        <input type="text" value={curdata.title} placeholder="Title" className="title" onChange={getData} />
                    </div>
                    <textarea value={curdata.comment} onChange={getData}>Content</textarea>
                    <button className="button"><AddIcon/></button>
                </div>            
                      
            </div>



        </>
    );
}
export default CreateNote;
