import React, {useState} from "react";

const ToDoList = (props) =>{

   const [repmsg, setRepmsg] = ([]);
   

    const reply = () =>{

        setRepmsg(["Thanks!"]);
    }

    
    return (
        <>
        <div style = {{ marginBottom : "20px" }}>

        <li>{props.name} <span className="Right"  onClick={() =>{props.userClick(props.id);
        }}>X</span> </li>
            
        <span>Edit</span> <span onClick={reply}>Reply</span>
        </div>
        </>            
    );

};

export default ToDoList;
