        setName1(event.target.value);
    };

    const getName = (event) =>{

        event.preventDefault();
        setLatname1(name1);
    };
   

    return(
    
        <>
        <div className = "main_box">

            <form onSubmit= {getName}>

            <div style = { { backgroundColor : bg } } >

            <button onClick = {changeBg} onDoubleClick = {clickDb}> {name}</button>

            <h2>Hi {latname1}</h2>

