import React, {useState} from 'react';

const App = () =>{

    const [letName, setLetName] = useState({

        name1: " ",
        name2: " ",
    });
 
    
    const fieldvalue = (event) =>{

        const name = event.target.name;
        const value = event.target.value;
       

        setLetName((test) =>{

            if(name === "fname"){
             
                return{
                name1: value,
                name2: test.name2,
            };
            }else if(name === "lname"){
             
                return{
                name1: test.name1,
                name2: value,
            };
        }

    });

};


    const getName = (event) =>{        
        event.preventDefault();
        
    };

    return (

        <div className="main_box">
        <form onSubmit={getName} >
        <div>

        <h2>Hello  {letName.name1} {letName.name2} </h2>
        <input 
            type="text" 
            placeholder="Enter your Name" 
            onChange={fieldvalue} 
            name="fname"            
            value={letName.name1}
        />

        <input 
            type="text" 
            placeholder="Enter your Lastname"  
            onChange={fieldvalue} 
            name="lname"            
            value={letName.name2}
        />

        <button type="submit">Click Me</button>
        </div>

        </form>
        </div>



    );


}
export default App;
