import React, { useState } from "react";

const imaPic = "https://picsum.photos/200/300";

const App = () =>{

    const [name, setName] = useState();
    const [list, setList] = useState([]);

    const getData = (event) =>{

        setName(event.target.value);
    };

    const displayCon = () =>{

        setList();

    }

    return(
        <>
        <div className="main_box">
            <div className="card_box">
            <h1 className="heading">Post</h1>
            
            <img src={imaPic} />
            <ol>
            {list.map((listData)=>{
                return (
                    <>
                    <li>{listData} <span className="Right">X</span></li>
                    <span>Edit</span> <span>Reply</span>
                    </>            
                );
            })}
                           
            </ol>
            <input type="text" placeholder=" " onChange={getData} />
            <button onClick={displayCon}>Comment</button>
            </div>
        </div>
        </>
    );

};

export default App;
