import React, {useState} from "react";

const ToDoList = (props) =>{

   const [repmsg, setRepmsg] = ("Thanks!");
   const [mianmsg, setMainmsg] = ([]);

    const reply = () =>{

        setMainmsg(repmsg);
    }

    
    return (
        <>
        <div style = {{ marginBottom : "20px" }}>

        <li>{props.name} <span className="Right"  onClick={() =>{props.userClick(props.id);
        }}>X</span> </li>
      
            {mianmsg.map((getMsg) =>{
            return<li>{getMsg}</li>;

            })}
            
        <span>Edit</span> <span onClick={reply}>Reply</span>
        </div>
        </>            
    );

};

export default ToDoList;
