import React, {useState} from "react";

const ToDoList = (props) =>{

    const [reply, setReply] = useState();
    const [disMsg, setDisMsg] = useState(); 

    const newMsg = (event) =>{
        setReply(event.target.value);
    };

    const replyMsg = () =>{

        setDisMsg(reply);

    };

    return (
        <>
        <div style = {{ marginBottom : "20px" }}>

        <li>{props.name} <span className="Right"  onClick={() =>{props.userClick(props.id);
        }}>X</span> </li>
            <ol><input type="text" onChange={newMsg} value={reply} />
                <li>{disMsg}</li>
            </ol>
        <span>Edit</span> <span onClick = {replyMsg}>Reply</span>
        </div>
        </>            
    );

};

export default ToDoList;
