import React, {useState} from 'react';

const App = () =>{

    const [name1, setName1] = useState();
    const [name2, setName2] = useState();

    const [letname1, setLetname1] = useState();
    const [letname2, setLetname2] = useState();

    const fieldOne = (event) =>{

        setName1(event.target.value);
    };

    const fieldTwo = (event) =>{

        setName2(event.target.value);
    };

    const getName = () =>{

        setLetname1(name1);
        setLetname2(name2);
    }

    return (

        <div className="main_box">

        <div>

        <h2>Hello {letname1} {letname2}</h2>
        <input type="text" placeholder="Enter your Name " onChange={fieldOne} value={name1}/>

        <input type="text" placeholder="Enter your Lastname "  onChange={fieldTwo} value={name2}/>

        <button onClick={getName}>Click Me</button>
        </div>

        </div>



    );


}
export default App;
