import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';


const CreateNote = () => {

    const [curdata, SetCurdata] = useState({
        title: " ",
        comment: " ",
    });



    const getData = (event) =>{
        
        const [name,value] = event.target;

        SetCurdata ((preVal)=>{
           return {
                ...preVal,
                [name]: value,
                };
        });
    };



    return(

        <>

                <div className="box_center">
                     
                  <div className="content">
                
                        <input 
                        type="text" 
                        name="title"
                        value={curdata.title} 
                        onChange={getData} 
                        placeholder="Title" 
                        className="title" 
                        

                        />
                    
                    <textarea 
                    rows=""
                    column=""
                    name="comment"
                    value={curdata.comment} 
                    onChange={getData}
                    placeholder="Write a note..."
                    >
                    Content
                    </textarea>
                    <button className="button"><AddIcon/></button>
                </div>            
                      
            </div>



        </>
    );
}
export default CreateNote;
