import React, {useState} from 'react';

const App = () =>{

    const [bgcol, setBgcol] = useState('#EEE8AA');

    const [letName, setLetName] = useState({

        name1 : "",
        name2 : "",
        field3 : "",
        field4 : "",

    });

    const fieldvalue = (event) => {

        const {name, value} = event.target;

        setLetName((preValue) => {
                       
            return{
                ...preValue,
                [name] : value,
            }

        });
    }
    
    const getName = (event) =>{        
        event.preventDefault();
        setBgcol('#FFC0CB');
       
        
    };

    return (

        <div className="main_box">
        <form onSubmit={getName} >
        <div style = {{ backgroundColor : bgcol }}>

        <h2>Hello  {letName.name1} {letName.name2} {letName.field3} {letName.field4}</h2>

        <input type="text" name="name1" placeholder="Enter your Name" onChange={fieldvalue} value={letName.name1}/>

        <input type="text" name="name2" placeholder="Enter your Lastname" onChange={fieldvalue} value={letName.name2}/>

        <input type="email" name="field3" placeholder="Enter your email" onChange={fieldvalue} value={letName.field3}/>

        <input type="number" name="field4" placeholder="Enter your phone number" onChange={fieldvalue} value={letName.field4}/>

        <button type="submit">Click Me</button>
        </div>

        </form>
        </div>



    );


}
export default App;
