import React, {useState} from 'react';

const App = () =>{

    const [letName, setLetName] = useState();
 
    
    const fieldvalue = (event) =>{

        console.log(event.target.value);
    };


    const getName = (event) =>{
        
        event.preventDefault();
        
    };

    return (

        <div className="main_box">
        <form onSubmit={getName} >
        <div>

        <h2>Hello  {letName.name1} {letName.name2} </h2>
        <input type="text" placeholder="Enter your Name " onChange={fieldvalue} value={letName.name1}/>

        <input type="text" placeholder="Enter your Lastname "  onChange={fieldvalue} value={letName.name2}/>

        <button type="submit">Click Me</button>
        </div>

        </form>
        </div>



    );


}
export default App;
