import React, { useState } from "react";
import ToDoList from "./ToDoList";

const imaPic = "https://picsum.photos/200/300";

const App = () =>{

    const [name, setName] = useState();
    const [list, setList] = useState([]);

    const getData = (event) =>{

        setName(event.target.value);
    };

    const displayCon = () =>{

        setList((preVal) =>{

            return[...preVal, name];
        });
        setName("");

    };

    const Deletecom = (id) =>{
        

       setList((preVal) => {

        return preVal.filter((arrEl, index) =>{

            return index !== id;

        });

       });

    };

    const [msg, setMsg] = useState();

    const userReply = (id) =>{
        console.log("deleted");

        setList((preVal) => {
        
        return preVal.filter((arrayElm, index) =>{

            return index === id ? setList("Thanks") : setList("");
           
        });
        });

    };

       return(
        <>
        <div className="main_box">
            <div className="card_box">
            <h1 className="heading">Post</h1>
            
            <img src={imaPic} />
           <ul>
            {list.map((listData,index)=>{

                return <ToDoList 
                    key={index}
                    id={index}
                    name ={listData} 
                    userClick = {Deletecom}
                    replyName = {msg}
                    reply = {userReply}
                     />                
                
            })}
                           
            </ul>
            <input type="text" value = {name}placeholder=" " onChange={getData} />
            <button onClick={displayCon}>Comment</button>
            </div>
        </div>
        </>
    );

};

export default App;
