import React, { useState } from "react";
import ToDoList from "./ToDoList";

const imaPic = "https://picsum.photos/200/300";

const App = () =>{

    const [name, setName] = useState();
    const [list, setList] = useState([]);

    const getData = (event) =>{

        setName(event.target.value);
    };

    const displayCon = () =>{

        setList((preVal) =>{

            return[...preVal, name];
        });
        setName("");

    }

    return(
        <>
        <div className="main_box">
            <div className="card_box">
            <h1 className="heading">Post</h1>
            
            <img src={imaPic} />
            <ol>
            {list.map((listData)=>{

                return <ToDoList name ={listData} />                
                
            })}
                           
            </ol>
            <input type="text" value = {name}placeholder=" " onChange={getData} />
            <button onClick={displayCon}>Comment</button>
            </div>
        </div>
        </>
    );

};

export default App;
