import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';


const CreateNote = (props) => {

    const [note, setNote] = useState({
        title: "",
        comment: "",
    }); 
    const storeData = (event) =>{
        
        const {name,value} = event.target;

        setNote((preVal) =>{
           return {
                ...preVal,
                [name]: value,
                };
        });
    };

    const getData = () =>{
        props.passnote(note);
        setNote({
            title: "",
            comment: "",
        });
    };

    return(
        <>      
        <div className="box_center">
            <div className="content">
                <input 
                type="text" 
                name="title"
                value={note.title} 
                onChange={storeData} 
                placeholder="Title" 
                className="title"
                />            
            <textarea 
            rows=""
            column=""
            name="comment"
            value={note.comment} 
            onChange={storeData}
            placeholder="Write a note..."
            ></textarea>
            <button onClick={getData} className="button"><AddIcon/></button>
        </div>            
    </div>
    </>
    );
}
export default CreateNote;
