import React, { useState } from "react";
import Header from "./Header";
import CreateNote from "./CreateNote";
import Note from "./Note";
import Footer from "./Footer";

const App = () =>{

    const [addItems, setAddItems] = useState([]);

    const addNote = (note) =>{
        // alert("I am Clicked");     
        setAddItems((oldData) => {
            return [...oldData, note];            
        });
    };

    const onDelete = (id) =>{
       setAddItems((lastdata) =>{
           lastdata.filter((arraE,indx) => {
            return indx !== id;
            });
        });
    };
    return(
        <>
        <Header/>
        <CreateNote passNote={addNote}  />        
        {addItems.map((val, index)=>{
            return <Note 
                    key={index}
                    id={index}
                    name={val.title}
                    content={val.comment}
                    deleteItem={onDelete}
            />
        })}
        
        <Footer/>
        </>
    );
}
export default App;
