import React from "react";
import AddIcon from '@material-ui/icons/Add';


const Content = () =>{

    const [curdata, SetCurdata] = ({
        title : "",
        content: "",
    });

    const storeData = (event) =>{
        
        const [name,value] = event.target;

        SetCurdata ((preVal)=>{
           return {
                ...preVal, curdata,
                [name] : value,
                }
        });
    };



    return(

        <>

                <div className="box_center">
                     
                  <div className="content">
                    <div>
                        <input type="text" value="" placeholder="Title" className="title" onChange={getData} />
                    </div>
                    <textarea value=""  onChange={getData}>Content</textarea>
                    <button className="button" onClick={storeData}><AddIcon/></button>
                </div>            
                      
            </div>



        </>
    );
}
export default Content;
