import React, { useState } from "react";
import Header from "./Header";
import CreateNote from "./CreateNote";
import Note from "./Note";
import Footer from "./Footer";

const App = () =>{

    const [addItems, setAddItems] = useState([]);
    const addNote = (note) =>{
        // alert("I am Clicked");     
        setAddItems((prevData) => {
            return [...prevData, note];            
        });
             
    };

    const onDelete = (id) => {
        alert("Are You Sure! want to Delete it.");
        setAddItems((olddata) => {
          return [...olddata.filter((arr,indx) =>{
            return indx !== id;
          })];            
      });    
           
    };
    

    return(
        <>
        <Header/>
        <CreateNote passNote={addNote}  />  
        
        {addItems.map((val, index) => {
            return (
                <Note 
                    key={index}
                    id={index}
                    title={val.title}
                    content={val.content}
                    deleteItem={onDelete}
            />
            );
        })}
        
        <Footer/>
        </>
    );
}
export default App;
