import React, {useState} from 'react';

const App = () =>{

    const [letName, setLetName] = useState({

        name1: " ",
        field2: " ",
        field3: " ",
        field4: " ",
    });
 
    
    const fieldvalue = (event) =>{

       const {name, value} = event.target;
    
        setLetName((preValue) =>{

            return{

                ...preValue,
                [name]: value,

            };
         
        });

    };

    const getName = (event) =>{        
        event.preventDefault();
        
    };

    return (

        <div className="main_box">
        <form onSubmit={getName} >
        <div>

        <h2>Hello  {letName.name1} {letName.name2} {letName.field3} {letName.field4}</h2>
        <input type="text" name="fname" placeholder="Enter your Name" onChange={fieldvalue} value={letName.name1}/>

        <input type="text" name="lname" placeholder="Enter your Lastname" onChange={fieldvalue} value={letName.field2}/>

        <input type="email" name="email" placeholder="Enter your email" onChange={fieldvalue} value={letName.field3}/>

        <input type="number" name="phone" placeholder="Enter your phone number" onChange={fieldvalue} value={letName.field4}/>

        <button type="submit">Click Me</button>
        </div>

        </form>
        </div>



    );


}
export default App;
