    // const bg = '#FFE4E1';

    const [bg, setBg] = useState('#FFE4E1');
    const [name, setName] = useState('Click Me');

    const bgchange = () =>{

        setBg('#FFE4E1');
        setName('We are Back ðŸ˜†');
    }

    const changeBg = () =>{

        setBg('#FFA07A');
        setName ('Clicked ðŸ˜„');

    };

    const clickDb = () =>{

        setBg('#8B0000');
        setName('DoubleClicked ðŸ˜');
    };

    const changeMybg = () =>{

        setBg('#ffffff');
        setName('Click Me!');

    };

    const [name1, setName1] = useState();
    const [letname1, setLetname1] = useState();

    const [name2, setName2] = useState();

    const [letname2, setLetname2] = useState();

   

    const password = (event) =>{

        setName2(event.target.value);

    }

    const username = (event) =>{

        setName1(event.target.value);
    };

    const getName = (event) =>{

        event.preventDefault();
        setLetname1(name1);
        setLetname2(name2);
    };
   

    return(
    
        <>
        <div className = "main_box">

            <form onSubmit= {getName}>

            <div style = { { backgroundColor : bg } }  onClick={bgchange}>

            <button onClick = {changeBg} onDoubleClick = {clickDb}> {name}</button>

            <h2>Hi {letname1} {letname2}</h2>

            <input type="text" placeholder="Enter your Name" onChange={username} vlaue={name1} />

            <input type="text" placeholder="Enter your Lastname" onChange={password} value={name2}/>


            <button type="submit" >Click Here</button>
            
            </div>
            </form>

        </div>
      
        </>
        );
}

export default App;
