import React, { useState } from "react";
import SendIcon from '@material-ui/icons/Send';
import DeleteIcon from '@material-ui/icons/Delete';
import Button from '@material-ui/core/Button';
import Tooltip from '@material-ui/core/Tooltip';



const App = () =>{

    const[name, setName] = useState();
    const [list, setList] = useState([]);

    const getData = (event) =>{

        setName(event.target.value);
    };

    const sendData = () => {
        setList((preVal)=>{

            return [...preVal, name];

        });
        
        setName(" ");
    };

    const [line, setLine] = useState(false);

    const checkline = () =>{
        console.log("deleted");
        
        setLine(true);

    };


    return(
        <>
        <div className="main_box">
        <div className="card_box">

        <h1>To Do List</h1>
        <input type="text" value = {name}placeholder="make List" onChange={getData} />
        <Button onClick={sendData}> <SendIcon  className="butGreen"/></Button>

        <ol>
        
            {list.map((cutData)=>{
                return  <li style = {{ textDecoration : line ? "line-through" : "none"}}>
                <span onclick={checkline} > <DeleteIcon /> </span>

                 {cutData}
                 </li>

            })}
            
        </ol>

        </div>
        </div>

   </>     
 );    
};
export default App;
