import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';


const CreateNote = () => {

    const [curdata, SetCurdata] = useState({
        title: " ",
        comment: " ",
    });
    const storeData = (event) =>{
        
        const {name,value} = event.target;

        SetCurdata((preVal) =>{
           return {
                ...preVal,
                [name]: value,
                };
        });
    };

    const getData = () =>{

    }

    return(
        <>      
        <div className="box_center">
            <div className="content">
                <input 
                type="text" 
                name="title"
                value={curdata.title} 
                onChange={storeData} 
                placeholder="Title" 
                className="title"
                />            
            <textarea 
            rows=""
            column=""
            name="comment"
            value={curdata.comment} 
            onChange={storeData}
            placeholder="Write a note..."
            ></textarea>
            <button onClick={getData} className="button"><AddIcon/></button>
        </div>            
    </div>
    </>
    );
}
export default CreateNote;
